import * as React from "react";
import Rating from "./Rating";
import DetailedPage from "../../interfaces/DetailedPage";
import { navigateToDetailedPage } from "../../../globalState/reducerActions";
import { Link } from "react-router-dom";
import { useAppDispatch } from "../../../globalState/hooks";
import Tag from "../Tag";

function FoodCard(props: DetailedPage) {
  const dispatch = useAppDispatch();
  return (
    <div
      className="col-xl-3 col-lg-4 col-md-5 col-sm-6 col-xs-12"
      style={{ marginLeft: "1vw", marginRight: "1vw"}}
    >
      <Link
        to={`/feed/${props.id}`}
        // onClick={() =>
        //   dispatch(
        //     navigateToDetailedPage({
        //       id: props.id,
        //       title: props.title,
        //       author: props.author,
        //       averageRating: props.averageRating,
        //       totalReviewCount: props.totalReviewCount,
        //       description: props.description,
        //       visiiblity: props.visiiblity,

        //     })
        //   )
        // }
      >
        <div
          className="card"
          style={{
            width: "18rem",
            margin: "1.5rem 0.6rem",
            padding: "0",
            display: "inline-block",
            overflow: "hidden",
          }}
        >
          <div
            className="card-body custom-card"
            style={{ whiteSpace: "normal" }}
          >
            <div className="d-flex justify-content-between">
              <div style={{color: "grey"}}>
                {props.author}
              </div>
              <div style={{color:"grey"}}>
                {props.visiiblity}
              </div>
            </div>
            <h5
              className="card-title"
              style={{
                height: "3rem",
                fontSize: "1rem",
                flexGrow: "12",
              }}
            >
              {props.title}
            </h5>
            {props.tags.map((tag, index) =>{
              <Tag text={tag} key={index}/>
            })}

            <Rating
              averageRating={props.averageRating ?? 0}
              totalReviewCount={props.totalReviewCount ?? 0}
            />
          </div>
        </div>
      </Link>
    </div>
  );
}

export default FoodCard;
