import * as React from "react";
import { useAppSelector } from "../../globalState/hooks";
import {useState, useRef} from "react"

interface ConfigurationPage {
  action: "add" | "edit";
}

function ConfigurationPage(props: ConfigurationPage) {
  const data = useAppSelector((state) => state.currentDetailedPage);
  const [name, setName] = useState("")
  const [description, setDescription] = useState("")
  const [numberOfRequests, setNumberOfRequests] = useState(0)
  const [keyword, setKeyword] = useState("")
  const [sourceLinks, setSourceLinks] = useState<string[]>(props.action === "add" ? [] : data.sourceLinks)
  const [checkBoxes, setCheckBoxes] = useState<boolean[]>([])
  if(props.action === "edit"){
    data.sourceLinks.forEach(source => {
        setCheckBoxes([...checkBoxes, false])
    })
  }
  const [sourceLink, setSourceLInk] = useState("")
//   const [checkBoxes, setCheckBoxes] = useState<React.MutableRefObject<HTMLInputElement>[]>([])


  const checkedIndexes: number[] = []


  function onChangeName(event:  React.ChangeEvent<HTMLInputElement>){
    setName(event.target.value)
  }
  function onChangeKeyword(event:  React.ChangeEvent<HTMLInputElement>){
    setName(event.target.value)
  }
  function onChangeNumber(event:  React.ChangeEvent<HTMLInputElement>){
    setNumberOfRequests(Number(event.target.value))

  }
  function onChangeDescription(event:  React.ChangeEvent<HTMLTextAreaElement>){
    setDescription(event.target.value)
  }
  function onChangeSourceLink(event:  React.ChangeEvent<HTMLInputElement>){
    setSourceLInk(event.target.value)
  }

  function onChangeCheckBox(event: React.ChangeEvent<HTMLInputElement>){
    const ind = Number(event.target.name)
    if (event.target.checked){
        checkedIndexes.push(ind)
        setCheckBoxes([...checkBoxes.slice(0,ind), true, ...checkBoxes.slice(ind+1)])
    } else{
        const index = checkedIndexes.indexOf(ind)
        checkedIndexes.splice(index,1)
        setCheckBoxes([...checkBoxes.slice(0,ind), false, ...checkBoxes.slice(ind+1)])
    }
    alert(checkedIndexes)
  }
  function addSourceLink(){
    if (sourceLinks.length > 0){
        setSourceLinks([...sourceLinks, sourceLink])
        setCheckBoxes([...checkBoxes, false])
    }else{
        setSourceLinks([sourceLink])
        setCheckBoxes([...checkBoxes, false])
    }
    setSourceLInk("")
  }
  function deleteSourceLinks(){
    checkedIndexes.forEach(index => {
        setSourceLinks([...sourceLinks.slice(0,index), ...sourceLinks.slice(index+1)])
        setCheckBoxes([...checkBoxes.slice(0,index), false, ...checkBoxes.slice(index+1)])
        setCheckBoxes([...checkBoxes.slice(0,index), ...checkBoxes.slice(index+1)])
    })

  }


  function submitHandler(event: React.FormEvent<HTMLFormElement>){
    event.preventDefault()
  }

  return (
    <div className="container" style={{ textAlign: "center" }}>
      <h1>Configure your feed</h1>
      <div className="row">
        <div className="col">
          <h2>Parameters</h2>
          {/* <form style={{ textAlign: "left" }}>
            <div className="mb-3">
              <label htmlFor="importSource" className="form-label">
                Import source
              </label>
              <input type="text" className="form-control" id="importSource" />
            </div>
            <button
              type="submit"
              className="btn custom-button"
              style={{ margin: "20px 0" }}
            >
              IMPORT
            </button>
          </form> */}
          <form style={{ textAlign: "left" }} onSubmit={(e) => submitHandler(e)}>
            <div className="mb-3">
              <label htmlFor="name" className="form-label">
                Name
              </label>
              {props.action === "edit" ? (
                <input
                  type="text"
                  className="form-control"
                  id="name"
                  name="name"
                  onChange={onChangeName}
                  value={data.title}/>
                // >{data.title}</input>
              ) : (
                <input
                  type="text"
                  className="form-control"
                  id="name"
                  name="name"
                  onChange={onChangeName}
                  value={name}
                />
              )}
            </div>
            <div className="mb-3">
              <label htmlFor="description" className="form-label">
                Description
              </label>
              {props.action === "edit" ? (
                <textarea
                  className="form-control"
                  id="description"
                  name="description"
                  rows={4}
                  value={data.description}
                  onChange={onChangeDescription}
                />
              ) : (
                <textarea
                  className="form-control"
                  id="description"
                  name="description"
                  rows={4}
                  onChange={onChangeDescription}
                  value={description}
                />
              )}
            </div>
            <div className="mb-3">
              <label htmlFor="numberOfRequests" className="form-label">
                Number of requests per video
              </label>
              {props.action === "edit" ? (
                <input
                  type="number"
                  className="form-control"
                  id="numberOfRequests"
                  name="numberOfRequests"
                  value={data.numberOfVideosPerRequest}
                  onChange={onChangeNumber}
                />
              ) : (
                <input
                  type="number"
                  className="form-control"
                  id="numberOfRequests"
                  name="numberOfRequests"
                  onChange={onChangeNumber}
                  value={numberOfRequests}
                />
              )}
            </div>
            <div className="mb-3">
              <label htmlFor="keyword" className="form-label">
                Keyword
              </label>
              {props.action === "edit" ? (
                <input
                  type="text"
                  className="form-control"
                  id="keyword"
                  name="keyword"
                  value={data.keyword}
                  onChange={onChangeKeyword}
                />
              ) : (
                <input
                  type="text"
                  className="form-control"
                  id="keyword"
                  name="keyword"
                  onChange={onChangeKeyword}
                  value={keyword}
                />
              )}
            </div>
            {/* <div className="mb-3">
              <label htmlFor="visibility" className="form-label">
                Visibility
              </label>
              <select className="form-select" id="visibility" name="visibility">
                <option value="Public">Public</option>
                <option value="Private">Private</option>
              </select>
            </div> */}

            <button
              type="submit"
              className="btn custom-button"
              style={{ margin: "20px 0" }}
            >
              SAVE
            </button>
          </form>
        </div>

        <div className="col">
          <h2>Add source</h2>
          <form style={{ textAlign: "left" }} onSubmit={(e) => submitHandler(e)}>
            <div className="mb-3">
              <label htmlFor="source" className="form-label">
                Source Link
              </label>
              <input
                type="text"
                className="form-control"
                id="source"
                name="source"
                value={sourceLink}
                onChange={onChangeSourceLink}
              />
            </div>
            <button
              type="submit"
              className="btn custom-button"
              style={{ margin: "20px 0" }}
              onClick={addSourceLink}
            >
              ADD
            </button>
          </form>
          <h2>Source Links</h2>
          <form style={{ textAlign: "left" }} onSubmit={(e) => submitHandler(e)}>
            {sourceLinks.map((link, ind) => {
              return (
                <div className="mb-3 form-check">
                  <input
                    type="checkbox"
                    className="form-check-input"
                    id= {link + ind}
                    checked={checkBoxes[ind]}
                    name={ind+""}
                    onChange={onChangeCheckBox}
                  />
                  <label className="form-check-label" htmlFor={link + ind}>
                    <div
                      style={{ borderBottom: "1px solid rgb(50, 191, 243)" }}
                    >
                      {link}
                    </div>
                  </label>
                </div>
              );
            })}

            <button
              type="submit"
              className="btn custom-button"
              style={{ margin: "20px 20px 20px 0px" }}
              onClick={deleteSourceLinks}
            >
              DELETE
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default ConfigurationPage;
