import * as React from "react";
import Tag from "../subcomponents/Tag";
import Rating from "../subcomponents/cards/Rating";
import {useAppSelector} from "../../globalState/hooks"


function DetailedPage() {
    const props = useAppSelector(state => state.currentDetailedPage)
  return (
    <div style={{textAlign: "center"}}>
      <h1>{props.title}</h1>
      <h2>{props.description}</h2>
      {props.tags.map((tag, id) => {
        return <Tag text={tag} key={id} />;
      })}
      <Rating averageRating={props.averageRating ?? 0} totalReviewCount={props.totalReviewCount ?? 0}/>
      <div className="row">
        <div className="col">
        <button type="button" className="btn" style={{backgroundColor: "purple", color:"white"}}>Primary</button>
        </div>
      </div>

    </div>
  );
}

export default DetailedPage;
